import { useEffect, useState } from "react";

import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";


const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch('https://foodappproject-93b6b-default-rtdb.firebaseio.com/meals.json');

      if (!response.ok) {
        throw new Error('Oh no! Somthing went wrong.')
      }

      const repsonseData = await response.json();

      const loadedMeals = [];

      for (const key in repsonseData) {
        loadedMeals.push({
          id: key,
          name: repsonseData[key].name,
          description: repsonseData[key].description,
          price: repsonseData[key].price,

        });
      }

      setMeals(loadedMeals);
      setIsLoading(false);
    };

    try {
      fetchMeals();
    } catch (error) {
      setIsLoading (false);
      setHttpError(error.message)
    };
  }, []);

  if (isLoading) {
    return (<section className={classes.MealsLoading}>
      <p>Loading...</p>
    </section>
    );
  };

  if (httpError) {
    return <section className={classes.MealsError}>
      <p>{httpError}</p>
    </section>
  }

  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
